import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import movieApi from "../../common/apis/movieApi";
import { APIKey } from "../../common/apis/movieApiKey";

// movie, series

export const fetchAsynkMovies = createAsyncThunk(
  "movies/fetchAsynkMovies",
  async () => {
    try {
      const moviesText = "Star";
      const { data } = await movieApi.get(
        `?apiKey=${APIKey}&s=${moviesText}&type=movie`
      );
      return data;
    } catch (error) {
      console.lof(error.response);
    }
  }
);

export const fetchAsynkShows = createAsyncThunk(
  "movies/fetchAsynkShows",
  async () => {
    try {
      const seriesText = "Friends";
      const { data } = await movieApi.get(
        `?apiKey=${APIKey}&s=${seriesText}&type=series`
      );
      return data;
    } catch (error) {
      console.lof(error.response);
    }
  }
);

const initialState = {
  movies: {
    data: {},
    loading: false,
    isSuccess: false,
    massage: "",
  },
  shows: {
    data: {},
    loading: false,
    isSuccess: false,
    massage: "",
  },
};

export const movieSlice = createSlice({
  name: "movies",
  initialState,
  reducer: {},
  extraReducers: {
    [fetchAsynkMovies.loading]: (state) => {
      state.movies.loading = true;
    },
    [fetchAsynkMovies.fulfilled]: (state, { payload }) => {
      state.movies.loading = false;
      state.movies.data = payload;
      state.movies.isSuccess = true;
    },
    [fetchAsynkMovies.rejected]: (state, { payload }) => {
      state.movies.massage = "failed";
    },
    [fetchAsynksShows.loading]: (state) => {
      state.shows.loading = true;
    },
    [fetchAsynkMovies.fulfilled]: (state, { payload }) => {
      state.shows.loading = false;
      state.shows.data = payload;
      state.shows.isSuccess = true;
    },
    [fetchAsynkShows.rejected]: (state, { payload }) => {
      state.shows.massage = "failed";
    },
  },
});

export default movieSlice.reducer;
export const getMovies = (state) => state.movies.movies.data;
export const getShows = (state) => state.movies.shows.data;
